import React, { useState, useEffect } from 'react';
import api from './service/api'

// Componente: Bloco isolado de HTML, CSS ou JS, o qual não interfere no restante da aplicação
// Propriedade: Informações que um componente Pai para o componente Filho
// Estado: Informações mantidas pelo Componente (Imutabilidade)

import './global.css'
import './App.css'
import './Sidebar.css'
import './Main.css'

import './components/DevItem'

function App() {
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');

  const [github_username, setGithubUsername] = useState('');
  const [techs, setTechs] = useState('');

  const [devs, setDevs] = useState([]);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;

        setLatitude(latitude);
        setLongitude(longitude);
      },
      (err) => {
        console.log(err);
      }, {
        timeout: 30000
      }
    );
  }, [])

  useEffect(() => {
    async function loadDevs() {
      const response = await api.get('/devs');

      setDevs(response.data);
    }

    loadDevs();
  }, [])

  async function handleAddDev(e) {
    e.preventDefault();

    const response = await api.post('/devs', { github_username, techs, latitude, longitude })

    setGithubUsername('')
    setTechs('')

    setDevs([...devs, response.data]);
  }

  return(
    <div id="app">
      <aside>
        <strong className="">Cadastrar</strong>
        <form onSubmit={handleAddDev}>
          <div className="input-block">
            <label htmlFor="github_username">Usuário do Github:</label>
            <input type="text" name="github_username" required id="github_username" 
              value={github_username}
              onChange={e => setGithubUsername(e.target.value)} />
          </div>

          <div className="input-block">
            <label htmlFor="techs">Tecnologias:</label>
            <input type="text" name="techs" required id="techs" 
              value={techs}
              onChange={e => setTechs(e.target.value)}  />
          </div>

          <div className="input-group">
            <div className="input-block">
              <label htmlFor="latitude">Latitude:</label>
              <input type="number" name="latitude" id="latitude" required 
                value={latitude} onChange={e => setLatitude(e.target.value)}  />
            </div>
            <div className="input-block">
              <label htmlFor="longitude">Longitude:</label>
              <input type="number" name="longitude" id="longitude" 
                value={longitude} onChange={e => setLongitude(e.target.value)} />
            </div>
          </div>

          <button type="submit">Salvar</button>
        </form>
      </aside>
      <main>
        <ul>
          {devs.map(dev => (
            <DevItem key={dev._id} dev={dev} />
          ))}
        </ul>
      </main>
    </div>
  )
}

export default App;
